    upper-left: "0,#{$height} #{$width},#{$height} #{$width},0",
    upper-right: "0,#{$height} #{$width},#{$height} 0,0",
    lower-left: "0,0 #{$width},#{$height} #{$width},0",
    lower-right: "0,0 #{$width},0 0,#{$height}"
);
$img:
    '<svg xmlns="http://www.w3.org/2000/svg" ' +
        'width="#{$width}" ' +
        'height="#{$height}" ' +
        'fill="#{$fill-rgb}" ' +
        'fill-opacity="#{$fill-alpha}"' +
    '><polygon points=\'#{map.get($points, $hypotenuse)}\'></polygon></svg>';

@if ($width == null) {
    $points: (
        upper-left: "0,#{$height} 100,#{$height} 100,0",
        upper-right: "0,#{$height} 100,#{$height} 0,0",
        lower-left: "0,0 100,#{$height} 100,0",
        lower-right: "0,0 100,0 0,#{$height}"
    );
    $img:
        '<svg xmlns="http://www.w3.org/2000/svg" ' +
            'preserveAspectRatio="none" ' +
            'viewBox="0 0 100 #{$height}" ' +
            'fill="#{$fill-rgb}" ' +
            'fill-opacity="#{$fill-alpha}"' +
        '><polygon points=\'#{map.get($points, $hypotenuse)}\'></polygon></svg>';
}
$top: auto;
$bottom: auto;
@if ($location == inside-top) {
    $top: 0;
} @else if ($location == outside-top) {
    $top: -$height * 1px;
} @else if ($location == inside-bottom) {
    $bottom: 0;
} @else if ($location == outside-bottom) {
    $bottom: -$height * 1px;
}
position: relative;
#{$selector} {
    content: "";
    position: absolute;
    z-index: 1;
    top: $top;
    right: 0;
    bottom: $bottom;
    left: 0;
    width: 100%;
    height: $height * 1px;
    background-image: url(svg-base64($img));
    background-position: center center;
    background-repeat: no-repeat;
    @if $fill-background {
        background-color: $fill-background;
    }
    @if ($width == null) {
        background-size: 100% 100%;
    }
    @content;
}

@if list.length($keywords) == 1 {
    $keyword: list.nth($keywords, 1);
    @if meta.type-of($keyword) == list {
        $keywords: $keyword;
    } @else if meta.type-of($keyword) == string and string.index($keyword, " ") {
        $keywords: str-split($keyword, " ");
    }
}
@each $keyword in $keywords {
    @extend #{escape-classname("%#{$keyword}")} !optional;
}

$length: list.length($colors);
@if $length > 1 {
 $stripes: ();
    @for $i from 1 through $length {
        $stripe: math.div(100%, $length) * ($i - 1);
        @if $i > 1 {
            $stripes: list.append($stripes, list.nth($colors, $i - 1) $stripe, comma);
        }
        $stripes: list.append($stripes, list.nth($colors, $i) $stripe, comma);
    }
    background-image: linear-gradient($direction, list.join((), $stripes, $separator: comma));
}
background-color: list.nth($colors, 1);

#{$selector} {
    content: "";
    display: table;
    clear: both;
}

max-width: $width;
margin-right: $margin;
margin-left: $margin;

@media only screen and (min-width: #{calc-add($width, 2 * $margin, px)}) {
    margin-right: auto;
    margin-left: auto;
}

position: $position;
#{$selector} {
    z-index: $z-index;
    @include position(0, 0, 0, 0, absolute, 100%, 100%);
    @if string.index($selector, ":before") or string.index($selector, ":after") {
        content: "";
    }
    @content;
}

@each $key, $value in $map {
    @if $key and $map {
        @if meta.type-of($value) == map {
            @if string.index($key + "", "@at-root ") == 1 {
                $key: str-replace($key + "", "@at-root ", "");
                @at-root {
                    @include declare-map((#{$key}: $value));
                }
            } @else {
                #{$key} {
                    @include declare-map($value);
                }
            }
        } @else {
            #{$key}: $value;
        }
    } @else if meta.type-of($value) != map {
        #{$key}: $value;
    }
}

example.code:
@include declare-map((color: red, margin: 0));                       /* color: red; margin: 0; */
@include declare-map((color: red, "&:hover": (color: blue)));         /* color: red; &:hover { color: blue; } */
@include declare-map((color: red, "&:hover, &:focus": (color: blue))); /* color: red; &:hover, &:focus { color: blue; } */

context.code:
position: $position;
background: $background;

> * {
  position: $position;
  z-index: $z-index;
}

&::before,
&::after {
  content: "";
  position: absolute;
  top: 0;
  width: 50vw;
  height: 100%;
  background: $background;
}

&::before {
  right: 50%;
}

&::after {
  left: 50%;
}

$col: list.nth($columns, 1);
$of: list.nth($columns, 3);
$selector-gap: selector-combine($selector, "&:nth-child(#{$of}n+1)");
$selector-vgap: selector-combine($selector, "&:nth-child(n+#{($of + 1)})");
$width: grid-column-width($columns, $gap);

display: flex;
justify-content: $justify-content;
align-items: $align-items;
flex-wrap: wrap;

#{$selector} {
    flex: 1 0 auto;
    width: $width;
    margin-left: $gap;
}

#{$selector-gap} {
    margin-left: 0;
}

#{$selector-vgap} {
    margin-top: $vgap;
}

@if $hide-backface {
    backface-visibility: hidden;
}
perspective: $perspective;

font-size: 0;

&::before,
&::after {
    color: $color;
    font-size: $font-size;
    font-family: $font-family;
    line-height: $line-height;
    text-align: $text-align;

    @content;
}

position: $position;
left: $left;
overflow: $overflow;
visibility: $visibility;
opacity: $opacity;

display: flex;
justify-content: $justify-content;
align-items: $align-items;
flex-wrap: $flex-wrap;
list-style: none;
padding-#{$side}: 0;

> * + * {
    margin-#{$side}: $gap;
}

$start: if($from < $to, $from, $to);
$end: if($from < $to, $to, $from);
@for $i from $start through $end {
    #{$selector} {
        #{if($from < $to, "&:nth-child(#{$i})", "&:nth-last-child(#{$i})")} {
            z-index: $z-index + $i - 1;
            @content;
        }
    }
}

padding-right: $margin;
padding-left: $margin;

#{$inner-selector} {
    max-width: $width;
    margin-right: auto;
    margin-left: auto;
}

@if not map.has-key($_keyframes, $name) {
    @keyframes #{$name} {
        @content;
    }
    $_keyframes: map.merge($_keyframes, (#{$name}: true)) !global;
}

